(()=>{"use strict";const e="a76bd9ccf221f5b2f6393608a4e47f8e";document.getElementById("submit").onclick=()=>s();const t=document.getElementById("city"),n=document.getElementById("location"),o=document.getElementById("temp"),a=document.getElementById("desc"),i=document.getElementById("feels"),c=document.getElementById("humidity"),d=document.getElementById("wind");let l=0,m=0;async function s(){t.value=t.value.split(" ").join("-");const s=await fetch((p=t.value,/\d/.test(p)?`http://api.openweathermap.org/geo/1.0/zip?zip=${t.value},GB&appid=${e}`:`http://api.openweathermap.org/geo/1.0/direct?q=${t.value.replace(" ","")}&appid=${e}`),{mode:"cors"});var p;const u=await s.json();console.log(u),/\d/.test(t.value)?(l=u.lat,m=u.lon):(l=u[0].lat,m=u[0].lon),console.log(`LAT:${l} LON:${m}`);const g=await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${l}&lon=${m}&appid=${e}`,{mode:"cors"}),r=await g.json();console.log(r),n.textContent=`${r.name}, ${r.sys.country}`,o.textContent=`${r.main.temp}`,a.textContent=`${r.weather[0].description}`,i.textContent=`Feels Like: ${r.main.feels_like}`,c.textContent=`Humidity: ${r.main.humidity}`,d.textContent=`Wind: ${r.wind.speed} M/Sec`}s()})();